function getResponseHeaders(){var hs={},r=new XMLHttpRequest();r.open('GET',document.location,false);r.send();r.getAllResponseHeaders().trim().split(/[\r\n]{1,2}/).forEach(function(l){var p=l.split(': '),h=p.shift(),v=p.join(': ');hs[h]=v;});return hs;}function getResponseHeader(k){return getResponseHeaders()[k.toLowerCase()];}
