function getResponseHeaders(){var r=new XMLHttpRequest();r.open('GET',document.location,false);r.send();var hs=r.getAllResponseHeaders();var arr=hs.trim().split(/[\r\n]{1,2}/),hM={};arr.forEach(function(l){var p=l.split(': '),h=p.shift(),v=p.join(': ');hM[h]=v;});return hM;}function getResponseHeader(k){return getResponseHeaders()[k.toLowerCase()];}
