if(!Date.now){Date.now=function(){return+new Date;};}function Timer(t){this.base=new Date(t==null?Date.now():t);}Timer.prototype.reset=function(t){return this.base=new Date(t==null?Date.now():t);};Timer.prototype.timing=function(t){this.time=new Date(t==null?Date.now():t);var ms=Math.max(this.time,this.base)-Math.min(this.time,this.base),sec=ms/1000,min=sec/60,hour=min/60,day=hour/24;return[Math.floor(day),Math.floor(hour%24),Math.floor(min%60),Math.floor(sec%60),ms%1000];};